/* RESET */
* { 
    margin:0;
    padding:0;
    box-sizing:border-box; 
    }
body 
{ 
    width: 100vw;
    height: 120vh;
    background:#0b0b0b; 
    color:#fff; 
    font-family:system-ui, Arial, sans-serif;

  background: url("../assets/pozadina2.png") no-repeat center center fixed;
  background-size: cover;
  margin: 0;
  font-family: 'Poppins', sans-serif;
  color: #fff;
}

.whole-page {
  height: 100%;
  width: 100%;
}



/* HEADER */
.site-header {
  position: relative;               /* zbog dropdowna koji se kači na 100% */
  background:#041d1e;
  padding:22px 24px;
  box-shadow:0 0 10px rgba(2, 225, 241, 0.263);
  z-index: 50;
  width: 100%;
  height: 15%;
}
.center{
    width: 100%;
    height: 70%;
}
.container {
  display:flex;
  align-items:center;
  gap:16px;
}

/* LOGO LEVO */
.brand { flex:0 0 auto; }
.brand img {
  display:block;
  height:56px; width:auto;
  filter: drop-shadow(0 0 10px #00ffff);
}

.logo {
  height:56px; width:auto;
  filter: drop-shadow(0 0 10px #00ffff);
}

/* NAV DESNO (desktop) */
.nav {
  margin-left:auto;                 /* gura nav udesno */
  display:flex;
  align-items:center;
  gap:28px;
  position: static;                 /* važno: nema overlaya na desktopu */
}

.nav a {
  color:#00ffff; text-decoration:none; font-size:17px;
  text-shadow:0 0 10px #00ffff; transition:.25s;
  margin-right: 2rem;
}
.nav a:hover { color:#fff; text-shadow:0 0 18px #00ffff, 0 0 28px #00ffff; }

/* HAMBURGER TASTER */
.nav-toggle {
  margin-left:auto;                 /* stoji skroz desno dok je nav sakriven */
  background:transparent; border:0; color:#00ffff;
  font-size:28px; cursor:pointer; line-height:1;
  display:none;                     /* skriva se na desktopu */
}

.home-info {
  position: absolute;
  top: 80%;
  bottom: 70%;
  right: 8%;
  margin-top: 9rem;
  transform: translateY(-50%);
  max-width: 550px;
  color: #d9ffff;
  text-align: center;
  line-height: 1.8;
  background: none;          /* nema pozadine */
  box-shadow: none;          /* nema svetle ivice */
  border: none;              /* nema okvira */
}

.home-info h1 {
  font-size: 3rem;
  color: #00ffff;
  text-shadow: 0 0 10px rgba(0, 255, 255, 0.4);
  margin-bottom: 20px;
}

.home-info p {
  font-size: 1.05rem;
  margin-bottom: 15px;
}

.home-info .highlight {
  color: #00ffff;
  font-weight: bold;
  text-shadow: 0 0 10px rgba(0, 255, 255, 0.6);
}
.site-footer {
  width: 100%;
  height: 15%;
  background: rgba(0, 0, 0, 0.85);
  border-top: 1px solid rgba(0, 255, 255, 0.2);
  text-align: center;
  padding: 25px 0;
  position: relative;
  bottom: 0;
  color: #aeefff;
  font-size: 0.95rem;
  letter-spacing: 0.5px;
  box-shadow: 0 -2px 15px rgba(0, 255, 255, 0.15);
}

.footer-content {
  max-width: 1000px;
  margin: 0 auto;
}

.footer-links a {
  color: #00ffff;
  text-decoration: none;
  transition: 0.3s ease;
}

.footer-links a:hover {
  color: #ffffff;
  text-shadow: 0 0 10px #00ffff;
}

/* Za dno stranice ako ima malo sadržaja */
body {
  min-height: 100vh;
  display: flex;
  flex-direction: column;
}

.site-footer {
  margin-top: auto;
}

/* MOBILNI RASPORED */
@media (max-width: 768px) {
  .nav-toggle { display:block; }

  /* sakrij nav dok nije otvoren */
  .nav {
    display:none;
    position:absolute;
    right:24px;
    top:100%;                       /* POENTA: ispod headera, ne preko logoa */
    background:#111;
    border:1px solid rgba(0,255,255,.35);
    box-shadow:0 6px 24px rgba(0,0,0,.5), 0 0 20px rgba(0,255,255,.15);
    padding:12px;
    border-radius:12px;
    flex-direction:column;
    gap:10px;
    z-index: 60;
  }

  /* kada je otvoren */
  .nav.open { display:flex; }

  /* da taster ostane desno a logo levo */
  .container { justify-content:flex-start; }
  .brand { order:0; }
  .nav-toggle { order:1; }
}


/* Centrirano i lepo prikazano i na manjim ekranima */
@media (max-width: 768px) {
  .home-info {
    position: static;
    transform: none;
    max-width: 90%;
    margin: 80px auto;
    text-align: center;
  }
