*{ margin:0; padding:0; box-sizing:border-box; }

html, body{
  height:100%;
}

/* Flex layout za lepljivi footer */
body{
  min-height:120vh;
  display:flex;
  flex-direction:column;
  font-family: 'Poppins', system-ui, Arial, sans-serif;
  color:#fff;
  background: var(--bg-dark) url("../assets/pozadina2.png") no-repeat center center fixed;
  background-size: cover;
}

/* ako koristiš .whole-page kao wrapper, samo da ne kvari flex na body-ju */
.whole-page{ width:100%; }

/* =========================
   HEADER
========================= */
.site-header{

  width:100%;
  height: 8%;;
  background: var(--header-bg);
  align-items: center;
  box-shadow:0 0 10px rgba(2,225,241,0.26);
  z-index:50;
  margin-bottom: 3rem;
  overflow: hidden;
}

.container{
  width: 100%;
  height: 100%;
  display:flex;
  align-items:center;
  justify-content:space-between;

}

/* LOGO LEVO */
.brand{
  flex: 2;
  display: flex;
  align-items: center;
}
.brand img,
.logo{
  display: flex;
  align-items: center;
  height: 3.5rem;
  filter: drop-shadow(0 0 10px var(--cyan));

}

/* NAV DESNO (desktop) */
.nav{
  height:100%;
  flex:1;
  margin-left:auto;
  display:flex;
  align-items:center;
  gap:28px;
}

.nav a{
  color:var(--cyan);
  text-decoration:none;
  font-size:17px;
  text-shadow:0 0 10px var(--cyan);
  transition:.25s;
  margin-bottom:3rem ;
}

.nav a:hover{
  color:#fff;
  text-shadow:0 0 18px var(--cyan), 0 0 28px var(--cyan);
}

/* HAMBURGER (skriven na desktopu) */
.nav-toggle{
  margin-left:auto;
  background:transparent;
  border:0;
  color:var(--cyan);
  font-size:28px;
  cursor:pointer;
  line-height:1;
  display:none;
}

/* =========================
   MAIN / HERO TEKST
========================= */
main{ 
  position:relative; 
  flex:1;                     /* gura footer na dno */
}

/* Wrapper ako ga koristiš */
.center{ width:100%; height:100%; }

/* Slogan / info blok na desnoj strani, vertikalno centriran */
.home-info{
  position:absolute;
  top:50%;
  right:8%;
  transform:translateY(-50%);
  max-width:550px;
  color:#d9ffff;
