  line-height:1.8;
  background:none;
  box-shadow:none;
  border:none;
}

.home-info h1{
  font-size:3rem;
  color:var(--cyan);
  text-shadow:0 0 10px rgba(0,255,255,0.4);
  margin-bottom:20px;
}

.home-info p{ font-size:1.05rem; margin-bottom:15px; }

.home-info .highlight{
  color:var(--cyan);
  font-weight:700;
  text-shadow:0 0 10px rgba(0,255,255,0.6);
}

/* =========================
   FOOTER
========================= */
.site-footer{
  width:100%;
  background: rgba(8, 32, 34, 0.85);
  border-top:1px solid rgba(0,255,255,0.2);
  text-align:center;
  padding:25px 0;
  color:#aeefff;
  font-size:.95rem;
  letter-spacing:.5px;
  box-shadow:0 -2px 15px rgba(0,255,255,0.15);
